using System.Collections.Generic; using UnityEngine; using System.Xml.Serialization; using System.Xml.Linq;  [XmlRoot("WeaponData")]  public class WeaponContainer {     [XmlArray("Weapons")]     [XmlArrayItem("Weapon")]     public List<Enemy> enemyList = new List<Enemy>();        public static XElement Load(string path)     {          TextAsset xml = Resources.Load<TextAsset>("Xml/" + path);          var doc = XDocument.Parse(xml.text);         return doc.Element("WeaponData");     } }